if (!defined("PHP_EOL")) define("PHP_EOL", "\r\n");

$email_newsletter    = $_POST['email_newsletter'];

if(trim($email_newsletter) == '') {
	echo '<div class="error_message">Please enter a valid email address.</div>';
	exit();
}
//$address = "your email address";
